import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => (
  <div className="h-screen w-screen bg-black text-white font-sans relative overflow-hidden">
    {/* Background image */}
    <img
      src="/assets/myimg.png" // Replace with actual path
      alt="Music homepage background"
      className="absolute inset-0 w-full h-full object-cover z-0"
    />

    {/* Top Navigation Bar */}
    <div className="absolute top-0 left-0 right-0 px-6 py-4 flex justify-between items-center z-30 bg-[#1a1a1a]/80 backdrop-blur-md border-b border-orange-500/20">
      {/* Left section */}
      <div className="flex items-center gap-8 text-sm md:text-base text-[#f5f5dc] font-medium">
        <Link to="/" className="hover:text-orange-400 transition duration-200">Home</Link>
        <span className="hover:text-orange-400 transition duration-200">Hi, Shashank</span>
      </div>

      {/* Right section */}
      <button className="text-sm md:text-base text-[#f5f5dc] hover:text-red-400 transition duration-200">
        Logout
      </button>
    </div>


    {/* Central Hero Section */}
    <div className="absolute inset-0 z-20 flex flex-col items-center justify-center text-center px-4">
      <h1 className="text-5xl md:text-6xl font-bold text-white tracking-wide mb-2">
        PANAMERA
      </h1>
      <p className="text-lg md:text-2xl text-white/80 mb-6">
        Where music connects to life
      </p>
      <button className="px-6 py-2 border border-white text-white rounded-md hover:bg-white hover:text-black transition">
        LISTEN NOW
      </button>

      {/* My Library Card */}
      <Link
        to="/library"
        className="mt-12 bg-white/10 hover:bg-white/20 text-white px-6 py-4 rounded-lg shadow-lg border border-white/20 transition text-xl font-semibold"
      >
        ðŸ“š My Library
      </Link>
    </div>

    {/* Visualizer (Optional) */}
    <div className="absolute bottom-6 right-10 z-20">
      <div className="flex gap-1">
        {[3, 6, 9, 6, 3].map((h, i) => (
          <div
            key={i}
            className="w-1 bg-orange-500 rounded"
            style={{
              height: `${h * 4}px`,
              animation: `pulse-opacity 1s ease-in-out ${i * 0.1}s infinite alternate`
            }}
          ></div>
        ))}
      </div>
    </div>
  </div>
);

export default Home;
