import { useEffect, useState } from 'react';
import { searchYouTube } from '../utils/youtube';

export default function Player({ track }) {
  const [videoUrl, setVideoUrl] = useState(null);

  useEffect(() => {
    const loadVideo = async () => {
      if (track?.name) {
        const url = await searchYouTube(`\${track.name} \${track.artists?.[0]?.name}`);
        setVideoUrl(url);
      } else {
        setVideoUrl(null);
      }
    };
    loadVideo();
  }, [track]);

  return (
    <div className="fixed bottom-0 left-0 right-0 bg-gray-900 text-white p-4 flex items-center justify-between shadow-md">
      <div className="text-sm">
        <span className="text-green-400 font-semibold">{track?.name}</span>
        {track?.artists?.[0]?.name && <span className="text-gray-400 ml-2">by {track.artists[0].name}</span>}
      </div>
      {videoUrl ? (
        <iframe width="320" height="60" src={videoUrl} title="YouTube player" frameBorder="0" allow="autoplay"></iframe>
      ) : (
        <span className="text-sm text-gray-400">No playable preview</span>
      )}
    </div>
  );
}
