import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import ChatBox from '../components/ChatBox';
import { getTracksByPlaylistId } from '../utils/spotify';

const PlaylistDetails = ({ token, setCurrentTrack }) => {
  const { id } = useParams();
  const [tracks, setTracks] = useState([]);

  useEffect(() => {
    getTracksByPlaylistId(token, id)
      .then((res) => setTracks(res.data.items))
      .catch((err) => console.error("Error fetching tracks:", err));
  }, [token, id]);

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6">Playlist Tracks</h2>
      <div className="space-y-4">
        {tracks.map(({ track }) => (
          <div key={track.id} className="bg-gray-800 rounded-xl shadow-md p-4 hover:bg-gray-700 transition-colors duration-200">
          <div className="flex justify-between items-center mb-2">
            <div>
              <div className="font-semibold text-white">{track.name}</div>
              <div className="text-sm text-gray-400">{track.artists?.[0]?.name}</div>
            </div>
            <button
              onClick={() => setCurrentTrack(track)}
              className="bg-green-600 hover:bg-green-700 text-white px-4 py-1 rounded-lg text-sm transition duration-150"
            >
              ▶️ Play
            </button>
          </div>

          <ChatBox songId={track.id || track.name} />
        </div>        
        ))}
      </div>
    </div>
  );
};

export default PlaylistDetails;
