import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getTracksByPlaylistId } from '../utils/spotify';

const PlaylistDetails = ({ token, setCurrentTrack }) => {
  const { id } = useParams();
  const [tracks, setTracks] = useState([]);

  useEffect(() => {
    getTracksByPlaylistId(token, id)
      .then((res) => setTracks(res.data.items))
      .catch((err) => console.error("Error fetching tracks:", err));
  }, [token, id]);

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6">Playlist Tracks</h2>
      <div className="space-y-2">
        {tracks.map(({ track }) => (
          <div key={track.id} className="flex justify-between items-center bg-gray-700 p-3 rounded">
            <div>
              <div className="font-semibold">{track.name}</div>
              <div className="text-sm text-gray-400">{track.artists?.[0]?.name}</div>
            </div>
            <button
              onClick={() => setCurrentTrack(track)}
              className="bg-green-500 hover:bg-green-600 text-white px-4 py-1 rounded text-sm"
            >
              ▶️ Play
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PlaylistDetails;
