import React from 'react';

const Home = () => (
  <div className="h-screen w-screen bg-black text-white font-sans relative overflow-hidden">
    {/* Background image */}
    <img
      src="/assets/myimg.png" // replace with the actual path to your artwork
      alt="Music homepage background"
      className="absolute inset-0 w-full h-full object-cover z-0"
    />

    {/* Top Navigation Bar */}
    <div className="absolute top-0 left-0 right-0 px-6 py-4 flex justify-between items-center z-30">
      <h1 className="text-lg md:text-xl font-semibold text-orange-400 tracking-wider flex items-center gap-1">
        <span>ðŸŽµ</span> My Music
      </h1>
      <div className="flex items-center gap-4 text-sm">
        <span className="text-gray-200">Hi, Shashank</span>
        <button className="text-gray-300 hover:underline">Logout</button>
      </div>
    </div>

    {/* Central Hero Content */}
    <div className="absolute inset-0 z-20 flex flex-col items-center justify-center text-center px-4">

      <button className="mt-6 px-6 py-2 border border-white text-white rounded-md hover:bg-white hover:text-black transition">
        LISTEN NOW
      </button>
    </div>

    {/* Optional: Audio visualizer or waveform styling */}
    <div className="absolute bottom-6 right-10 z-20">
      <div className="flex gap-1">
        {[3, 6, 9, 6, 3].map((h, i) => (
          <div
            key={i}
            className="w-1 bg-orange-500 rounded"
            style={{
              height: `${h * 4}px`,
              animation: `pulse-opacity 1s ease-in-out ${i * 0.1}s infinite alternate`
            }}
          ></div>
        ))}
      </div>
    </div>
  </div>
);

export default Home;
