import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const BackgroundScene = () => {
  const mountRef = useRef();

  useEffect(() => {
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth/window.innerHeight, 0.1, 1000);
    camera.position.z = 5;

    const renderer = new THREE.WebGLRenderer({ alpha: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    mountRef.current.appendChild(renderer.domElement);

    // ðŸŽµ Speaker geometry
    const speakerGeometry = new THREE.CylinderGeometry(1, 1, 2, 32);
    const speakerMaterial = new THREE.MeshBasicMaterial({ color: 0xff5555 });
    const speaker = new THREE.Mesh(speakerGeometry, speakerMaterial);
    scene.add(speaker);

    // ðŸŽ¶ Soundwave rings
    const rings = [];
    const ringMaterial = new THREE.LineBasicMaterial({ color: 0xffffff, transparent: true, opacity: 0.5 });
    for (let i = 0; i < 5; i++) {
      const ringGeometry = new THREE.RingGeometry(1.2 + i * 0.3, 1.4 + i * 0.3, 64);
      const ring = new THREE.Line(ringGeometry, ringMaterial);
      ring.rotation.x = Math.PI / 2;
      scene.add(ring);
      rings.push(ring);
    }

    // ðŸ”„ Animate
    const animate = () => {
      requestAnimationFrame(animate);
      rings.forEach((ring, idx) => {
        ring.scale.x += 0.01;
        ring.scale.y += 0.01;
        if (ring.scale.x > 2 + idx) {
          ring.scale.set(1, 1, 1);
        }
      });
      renderer.render(scene, camera);
    };
    animate();

    // Cleanup
    return () => {
      mountRef.current.removeChild(renderer.domElement);
    };
  }, []);

  return <div ref={mountRef} className="absolute inset-0 z-0" />;
};

export default BackgroundScene;
