import React from 'react';
import BackgroundScene from '../components/BackgroundScene';

const Home = () => (
  <div className="h-full flex items-center justify-center bg-black">
    <div className="relative w-full max-w-5xl mx-auto aspect-[16/9] overflow-hidden rounded-xl">
      {/* Background with musical connection illustration */}
      <div className="absolute inset-0 bg-gradient-to-br from-spotify-burgundy/40 via-black to-black z-10"></div>
      <img
        src="/assets/20250505_1107_Musical Connection_remix_01jtgfpf09evva0vcfm2yxm4t1.png"
        alt="Music connects life illustration"
        className="absolute inset-0 w-full h-full object-cover z-0"
      />

      {/* Welcome text in the top-left corner */}
      <div className="absolute top-6 left-6 z-20">
        <h1 className="text-3xl md:text-5xl font-bold text-white tracking-wider animate-fade-in">
          Welcome
        </h1>
      </div>

      {/* Tagline text under the characters */}
      <div className="absolute bottom-24 w-full text-center z-20">
        <p className="text-lg md:text-2xl text-white/90 animate-pulse-opacity">
          Where music connects to life
        </p>
      </div>

      {/* Decorative audio wave animation (speaker pulse) */}
      <div className="absolute bottom-10 left-0 right-0 flex justify-center gap-1 z-20">
        {[...Array(7)].map((_, i) => (
          <div
            key={i}
            className="w-1 md:w-2 rounded-full bg-spotify-burgundyLight"
            style={{
              height: `${10 + (i % 3) * 10}px`,
              animation: `pulse-opacity ${1 + i * 0.1}s ease-in-out infinite alternate`,
              animationDelay: `${i * 0.1}s`
            }}
          ></div>
        ))}
      </div>
    </div>
  </div>
);

export default Home;
