import React from 'react';

const Home = () => (
  <div className="h-screen w-screen bg-black text-white font-sans relative overflow-hidden">
    {/* Background image - no crop */}
    <img
      src="/assets/20250505_1107_Musical Connection_remix_01jtgfpf09evva0vcfm2yxm4t1.png"
      alt="Music connects life illustration"
      className="absolute inset-0 w-full h-full object-contain md:object-cover z-0"
      style={{ objectPosition: 'center' }}
    />

    {/* Overlay to darken image slightly */}
    <div className="absolute inset-0 bg-black/50 z-10" />

    {/* Navigation bar */}
    <div className="absolute top-0 left-0 right-0 px-6 py-4 z-30 flex justify-between items-center bg-transparent">
      <h1 className="text-xl font-bold text-[#A0522D] tracking-wider">ðŸŽµ My Music</h1>
      <div className="flex items-center gap-4">
        <span className="text-sm text-white/80">Hi, Shashank</span>
        <button className="text-sm text-red-400 hover:underline">Logout</button>
      </div>
    </div>

    {/* Center content - headline + CTA */}
    <div className="absolute inset-0 z-20 flex flex-col items-center justify-center text-center px-4">
      <h1 className="text-4xl md:text-6xl font-extrabold tracking-wide drop-shadow-md">
        PANAMERA
      </h1>
      <p className="mt-4 text-lg md:text-2xl text-white/80">
        Where music connects to life
      </p>
      <button className="mt-8 px-6 py-2 border border-white text-white rounded-md hover:bg-white hover:text-black transition">
        LISTEN NOW
      </button>
    </div>

    {/* Decorative sound bar pulse (optional) */}
    <div className="absolute bottom-6 left-0 right-0 flex justify-center gap-1 z-20">
      {[...Array(7)].map((_, i) => (
        <div
          key={i}
          className="w-1 md:w-2 rounded-full bg-[#A0522D]"
          style={{
            height: `${10 + (i % 3) * 10}px`,
            animation: `pulse-opacity ${1 + i * 0.1}s ease-in-out infinite alternate`,
            animationDelay: `${i * 0.1}s`
          }}
        ></div>
      ))}
    </div>
  </div>
);

export default Home;
