import React from 'react';
import BackgroundScene from '../components/BackgroundScene';

const Home = () => (
  <div className="h-screen w-screen bg-black text-white font-sans">
    <div className="relative w-full h-full overflow-hidden">
      {/* Fullscreen Background Illustration */}
      <img
        src="/assets/20250505_1107_Musical Connection_remix_01jtgfpf09evva0vcfm2yxm4t1.png"
        alt="Music connects life illustration"
        className="absolute inset-0 w-full h-full object-cover z-0"
      />

      {/* Top Navigation Bar */}
      <div className="absolute top-0 left-0 right-0 px-6 py-3 bg-black/70 backdrop-blur-md z-30 flex justify-between items-center">
        <h1 className="text-xl font-bold text-[#A0522D] tracking-wide">ðŸŽµ My Music</h1>
        <div className="flex items-center gap-4">
          <span className="text-sm text-gray-300">Hi, Shashank</span>
          <button className="text-sm text-red-400 hover:underline">Logout</button>
        </div>
      </div>

      {/* Welcome text in the top-left corner */}
      <div className="absolute top-24 left-10 z-20">
        <h1 className="text-5xl md:text-6xl font-bold text-white tracking-wider animate-fade-in">
          Welcome
        </h1>
      </div>

      {/* Tagline text placed lower, below the image figures */}
      <div className="absolute bottom-12 w-full text-center z-20">
        <p className="text-lg md:text-2xl text-white/90 animate-pulse-opacity">
          Where music connects to life
        </p>
      </div>

      {/* Decorative speaker pulse animation */}
      <div className="absolute bottom-4 left-0 right-0 flex justify-center gap-1 z-20">
        {[...Array(7)].map((_, i) => (
          <div
            key={i}
            className="w-1 md:w-2 rounded-full bg-[#A0522D]"
            style={{
              height: `${10 + (i % 3) * 10}px`,
              animation: `pulse-opacity ${1 + i * 0.1}s ease-in-out infinite alternate`,
              animationDelay: `${i * 0.1}s`
            }}
          ></div>
        ))}
      </div>
    </div>
  </div>
);

export default Home;
