import React from 'react';
import BackgroundScene from '../components/BackgroundScene';

const Home = () => (
  <div className="h-full flex items-center justify-center bg-black">
    <div className="relative w-full max-w-5xl mx-auto aspect-[16/9] overflow-hidden rounded-xl">
      {/* Abstract music/life connection image */}
      <div className="absolute inset-0 bg-gradient-to-br from-spotify-burgundy/40 via-black to-black z-10"></div>
      <img
        src="https://images.unsplash.com/photo-1470813740244-df37b8c1edcb"
        alt="Music connects life"
        className="absolute inset-0 w-full h-full object-cover"
      />

      {/* Welcome text with animation */}
      <div className="absolute inset-0 flex flex-col items-center justify-center z-20">
        <h1 className="text-7xl md:text-9xl font-bold text-white tracking-wider animate-fade-in">
          Welcome
        </h1>
        <p className="mt-4 text-xl text-white/80 animate-pulse-opacity">
          Where music connects to life
        </p>
      </div>

      {/* Decorative speaker soundwave animation */}
      <div className="absolute bottom-10 left-0 right-0 flex justify-center items-end gap-1 z-20">
        <div className="w-4 h-4 bg-white rounded-full mb-1 animate-bounce"></div>
        {[...Array(6)].map((_, i) => (
          <div
            key={i}
            className="w-1 md:w-2 bg-spotify-burgundyLight rounded-full"
            style={{
              height: `${8 + i * 4}px`,
              animation: `pulse-opacity ${0.7 + i * 0.1}s ease-in-out infinite alternate`,
              animationDelay: `${i * 0.1}s`
            }}
          ></div>
        ))}
        <div className="w-4 h-4 bg-white rounded-full mb-1 animate-bounce"></div>
      </div>
    </div>
  </div>
);

export default Home;
