import { useEffect, useState } from 'react';
import { db } from '../firebase';
import {
  collection,
  addDoc,
  query,
  orderBy,
  onSnapshot,
  serverTimestamp
} from 'firebase/firestore';

const ChatBox = ({ songId }) => {
  const [message, setMessage] = useState('');
  const [comments, setComments] = useState([]);
  const [showAll, setShowAll] = useState(false);

  useEffect(() => {
    if (!songId) return;

    const q = query(
      collection(db, 'comments', songId, 'messages'),
      orderBy('timestamp', 'asc')
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      setComments(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    });

    return () => unsubscribe();
  }, [songId]);

  const sendComment = async () => {
    if (!message.trim()) return;
    const user = localStorage.getItem("spotify_display_name") || "User";

    await addDoc(collection(db, 'comments', songId, 'messages'), {
      text: message,
      user,
      timestamp: serverTimestamp()
    });

    setMessage('');
  };

  const visibleComments = showAll ? comments : comments.slice(-2);

  return (
    <div className="bg-gray-900 text-white mt-3 p-3 rounded-lg shadow-md">
      <h4 className="text-sm font-semibold mb-2">ðŸ’¬ Discussion</h4>

      <div className="space-y-2 text-sm mb-3 max-h-40 overflow-y-auto">
        {visibleComments.map((c) => (
          <div key={c.id} className="bg-gray-700 px-3 py-1 rounded">
            <span className="text-green-400 font-semibold">{c.user}:</span> {c.text}
          </div>
        ))}
        {comments.length > 2 && !showAll && (
          <button
            onClick={() => setShowAll(true)}
            className="text-blue-400 text-xs hover:underline"
          >
            Show more comments...
          </button>
        )}
      </div>

      <div className="flex gap-2">
        <input
          className="flex-grow p-2 rounded bg-gray-800 text-white text-sm"
          placeholder="Write your thoughts..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button
          onClick={sendComment}
          className="bg-green-600 px-3 py-1 text-sm rounded hover:bg-green-700"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBox;
