import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { getTokenFromUrl, loginUrl } from './utils/auth';
import Home from './pages/Home';
import Library from './components/Library';
import Sidebar from './components/Sidebar';
import PlaylistDetails from './pages/PlaylistDetails';
import Player from './components/Player';

const App = () => {
  const [token, setToken] = useState(null);
  const [currentTrack, setCurrentTrack] = useState(null);

  useEffect(() => {
    const hash = getTokenFromUrl();
    window.location.hash = '';
    const _token = hash.access_token;

    if (_token) {
      localStorage.setItem("spotify_token", _token);
      setToken(_token);

      fetch("https://api.spotify.com/v1/me", {
        headers: { Authorization: `Bearer ${_token}` },
      })
        .then(res => res.json())
        .then(data => {
          localStorage.setItem("spotify_display_name", data.display_name || "User");
        })
        .catch(() => {
          // Token might be expired or invalid
          localStorage.clear();
          window.location.href = loginUrl;
        });
    } else {
      const storedToken = localStorage.getItem("spotify_token");
      if (storedToken) {
        setToken(storedToken);
      } else {
        window.location.href = loginUrl;
      }
    }
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    // Start Spotify logout
    window.location.href = "https://accounts.spotify.com/en/logout";

    // After 1.5s delay, return to your app
    setTimeout(() => {
      window.location.href = "http://localhost:3000";
    }, 1500);
  };


  return (
    <BrowserRouter>
      {/* üîù Top Bar */}
      <div className="bg-gray-900 text-white px-6 py-3 flex justify-between items-center">
        <h1 className="text-xl font-bold text-green-400">üéµ Music App</h1>
        <button
          onClick={handleLogout}
          className="text-sm text-red-400 hover:underline"
        >
          Logout
        </button>
      </div>

      {/* üì¶ Layout */}
      <div className="flex">
        <Sidebar />
        <div className="flex-grow">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/library" element={<Library token={token} />} />
            <Route path="/playlist/:id" element={<PlaylistDetails token={token} setCurrentTrack={setCurrentTrack} />} />
          </Routes>
        </div>
      </div>

      <Player track={currentTrack} setCurrentTrack={setCurrentTrack} />
    </BrowserRouter>
  );
};

export default App;
