import { useEffect, useState } from 'react';
import { db } from '../firebase';
import {
  collection,
  addDoc,
  query,
  orderBy,
  onSnapshot,
  serverTimestamp,
  doc,
  updateDoc
} from 'firebase/firestore';

const emojis = ['ðŸ”¥', 'ðŸ˜¢', 'ðŸ˜¡', 'ðŸ˜²', 'â¤ï¸'];

const ChatBox = ({ songId, currentUserName }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [replyingTo, setReplyingTo] = useState(null);

  useEffect(() => {
    if (!songId) return;

    const q = query(
      collection(db, 'comments', songId, 'messages'),
      orderBy('timestamp', 'asc')
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const msgs = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));

      // Group top-level messages and replies
      const grouped = msgs.reduce((acc, msg) => {
        if (!msg.parentMessageId) {
          acc.push({ ...msg, replies: [] });
        } else {
          const parent = acc.find(m => m.id === msg.parentMessageId);
          if (parent) {
            parent.replies.push(msg);
          }
        }
        return acc;
      }, []);

      setMessages(grouped);
    });

    return () => unsubscribe();
  }, [songId]);

  const sendMessage = async () => {
    if (!message.trim()) return;

    await addDoc(collection(db, 'comments', songId, 'messages'), {
      text: message,
      user: currentUserName,
      timestamp: serverTimestamp(),
      parentMessageId: replyingTo ? replyingTo.id : null,
      reactions: {}  // Initialize reactions
    });

    setMessage('');
    setReplyingTo(null);
  };

  const toggleReaction = async (messageId, emoji) => {
    const msgRef = doc(db, 'comments', songId, 'messages', messageId);

    const messageSnapshot = await msgRef.get();
    const data = messageSnapshot.data();
    const currentReactions = data.reactions || {};

    const userList = currentReactions[emoji] || [];
    const updatedList = userList.includes(currentUserName)
      ? userList.filter(user => user !== currentUserName)
      : [...userList, currentUserName];

    await updateDoc(msgRef, {
      [`reactions.${emoji}`]: updatedList
    });
  };

  return (
    <div className="bg-gray-800 text-white p-4 rounded mt-4 max-h-60 overflow-y-auto">
      <h3 className="text-lg font-semibold mb-2">ðŸ’¬ Chat for this Song</h3>

      {messages.map((msg) => (
        <div key={msg.id} className="mb-3">
          <div className="bg-gray-700 p-2 rounded">
            <span className="font-semibold">{msg.user}</span>
            <div>{msg.text}</div>

            {/* Emoji Reactions */}
            <div className="flex gap-2 mt-2">
              {emojis.map((emoji) => (
                <button
                  key={emoji}
                  onClick={() => toggleReaction(msg.id, emoji)}
                  className={`text-xl ${
                    msg.reactions?.[emoji]?.includes(currentUserName)
                      ? 'opacity-100'
                      : 'opacity-50'
                  }`}
                >
                  {emoji} {msg.reactions?.[emoji]?.length || 0}
                </button>
              ))}
            </div>

            <button
              className="text-sm text-green-400 mt-1"
              onClick={() => {
                setReplyingTo(msg);
                setMessage(`@${msg.user} `);
              }}
            >
              Reply
            </button>
          </div>

          {/* Replies */}
          {msg.replies.map((reply) => (
            <div key={reply.id} className="ml-6 mt-1 bg-gray-600 p-2 rounded">
              <span className="font-semibold">{reply.user}</span>
              <div>{reply.text}</div>
              {/* Emoji reactions for replies */}
              <div className="flex gap-2 mt-2">
                {emojis.map((emoji) => (
                  <button
                    key={emoji}
                    onClick={() => toggleReaction(reply.id, emoji)}
                    className={`text-xl ${
                      reply.reactions?.[emoji]?.includes(currentUserName)
                        ? 'opacity-100'
                        : 'opacity-50'
                    }`}
                  >
                    {emoji} {reply.reactions?.[emoji]?.length || 0}
                  </button>
                ))}
              </div>
            </div>
          ))}
        </div>
      ))}

      {/* Input */}
      <div className="flex gap-2 mt-2">
        <input
          className="flex-grow p-2 rounded bg-gray-900 text-white"
          placeholder={
            replyingTo ? `Replying to ${replyingTo.user}` : 'Write a comment...'
          }
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button
          onClick={sendMessage}
          className="bg-green-600 px-3 rounded hover:bg-green-700"
        >
          Send
        </button>
      </div>

      {/* Cancel reply */}
      {replyingTo && (
        <div className="text-sm mt-1 text-gray-300">
          Replying to <strong>{replyingTo.user}</strong>{' '}
          <button onClick={() => setReplyingTo(null)} className="text-red-400 ml-2">
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};

export default ChatBox;
